import { useState } from 'react';
import apiService from '../apiService';

const AddBrandForm = ({ onBrandAdded, onCancel }) => {
  const [newBrandName, setNewBrandName] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    if (!newBrandName.trim()) {
      alert("O nome da marca não pode ser vazio.");
      setLoading(false);
      return;
    }

    try {
      await apiService.createBrand({ name: newBrandName.trim() });
      alert('Marca salva com sucesso!');
      onBrandAdded();
    } catch (error) {
      console.error("Erro ao adicionar marca:", error);
      alert("Falha ao adicionar marca. Tente novamente.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white p-8 rounded-2xl shadow-xl w-full border border-gray-200">
      <h2 className="text-2xl font-bold text-gray-800 mb-6">Adicionar Nova Marca</h2>
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block text-gray-700 text-sm font-medium mb-2" htmlFor="newBrandName">Nome da Marca</label>
          <input
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            type="text"
            id="newBrandName"
            value={newBrandName}
            onChange={(e) => setNewBrandName(e.target.value)}
            placeholder="Digite o nome da nova marca"
            required
          />
        </div>
        <div className="flex justify-end space-x-4 mt-6">
          <button
            type="button"
            onClick={onCancel}
            className="px-6 py-3 border border-gray-300 text-gray-700 font-bold rounded-lg hover:bg-gray-100 transition-colors duration-200"
          >
            Cancelar
          </button>
          <button
            type="submit"
            className="px-6 py-3 bg-blue-600 text-white font-bold rounded-lg hover:bg-blue-700 transition duration-300 shadow-md disabled:bg-blue-300"
            disabled={loading}
          >
            {loading ? 'Salvando...' : 'Salvar Marca'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddBrandForm;
