import { useState, useEffect } from 'react';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import apiService from '../apiService';
import OrdersForm from '../components/OrdersForm';
import ConfirmationModal from '../components/ConfirmationModal'; // <-- importante

const OrdersPage = ({ userId, reps, brands }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [modalConfig, setModalConfig] = useState(null); // { type: 'delete'|'finalize', orderId }
  const [clients, setClients] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [ordersData, clientsData] = await Promise.all([
          apiService.getOrders(userId),
          apiService.getClients(),
        ]);
        setOrders(ordersData || []);
        const map = {};
        (clientsData || []).forEach(c => { map[c.id] = c.companyName; });
        setClients(map);
      } catch (err) {
        console.error('Erro ao buscar dados:', err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [userId]);

  const openModal = (type, orderId) => setModalConfig({ type, orderId });
  const closeModal = () => setModalConfig(null);

  const confirmDelete = async () => {
    if (!modalConfig) return;
    try {
      await apiService.deleteOrder(modalConfig.orderId);
      setOrders(prev => prev.filter(o => o.id !== modalConfig.orderId));
    } catch (err) {
      console.error('Erro ao excluir pedido:', err);
      alert('Falha ao excluir o pedido. Tente novamente.');
    } finally {
      closeModal();
    }
  };

  const confirmFinalize = async () => {
    if (!modalConfig) return;
    try {
      await apiService.finalizeOrder(modalConfig.orderId);
      setOrders(prev => prev.map(o => o.id === modalConfig.orderId ? { ...o, status: 'Entregue', finishedAt: new Date().toISOString() } : o));
    } catch (err) {
      console.error('Erro ao finalizar pedido:', err);
      alert('Falha ao finalizar o pedido. Tente novamente.');
    } finally {
      closeModal();
    }
  };

  const handleGeneratePdf = (order) => {
    const doc = new jsPDF();
    doc.setFontSize(14);
    doc.text(`Pedido #${order.id}`, 10, 10);
    doc.setFontSize(11);
    doc.text(`Cliente: ${clients[order.clientId] || 'N/A'}`, 10, 18);
    doc.text(`Vendedor: ${reps[order.userId] || 'N/A'}`, 10, 26);
    doc.text(`Data: ${order.finishedAt ? new Date(order.finishedAt).toLocaleDateString('pt-BR') : 'N/A'}`, 10, 34);

    const columns = ["Produto", "Marca", "Qtd", "Preço Unit.", "Total"];
    const rows = (order.items || []).map(it => [
      it.productName,
      it.brand,
      it.quantity,
      `R$ ${parseFloat(it.price).toFixed(2)}`,
      `R$ ${(parseFloat(it.price) * it.quantity).toFixed(2)}`
    ]);

    doc.autoTable({ head: [columns], body: rows, startY: 45, theme: 'grid', headStyles: { fillColor: [230,230,230] }, styles: { fontSize: 10 } });

    const finalY = doc.lastAutoTable?.finalY || 45;
    const discountAmount = (parseFloat(order.totalPrice || 0) * ((parseFloat(order.discount) || 0) / 100));
    doc.text(`Desconto: R$ ${discountAmount.toFixed(2)} (${order.discount || 0}%)`, 10, finalY + 8);
    doc.text(`Valor Total: R$ ${parseFloat(order.totalPrice || 0).toFixed(2)}`, 10, finalY + 16);

    doc.save(`pedido_${order.id}.pdf`);
  };

  if (loading) return <div className="p-8 text-center text-gray-500">Carregando pedidos...</div>;

  if (showForm) {
    return (
      <div className="p-8">
        <OrdersForm
          userId={userId}
          clients={clients}
          brands={brands}
          onOrderAdded={(newOrder) => {
            setOrders(prev => [...prev, newOrder]);
            setShowForm(false);
          }}
          onCancel={() => setShowForm(false)}
        />
      </div>
    );
  }

  return (
    <div className="p-8">
      {/* Modal */}
      {modalConfig && (
        <ConfirmationModal
          show={!!modalConfig}
          onConfirm={modalConfig.type === 'delete' ? confirmDelete : confirmFinalize}
          onCancel={closeModal}
          message={modalConfig.type === 'delete' ? 'Confirma exclusão do pedido?' : 'Confirma finalizar o pedido?'}
          confirmText={modalConfig.type === 'delete' ? 'Excluir' : 'Finalizar'}
          confirmColor={modalConfig.type === 'delete' ? 'bg-red-600 hover:bg-red-700' : 'bg-green-600 hover:bg-green-700'}
        />
      )}

      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-800">Gerenciamento de Pedidos</h1>
        <button onClick={() => setShowForm(true)} className="bg-blue-600 text-white px-4 py-2 rounded">+ Criar Pedido</button>
      </div>

      <div className="grid gap-4 md:grid-cols-2">
        {orders.length === 0 ? (
          <div className="text-center text-gray-500 col-span-full">Nenhum pedido encontrado.</div>
        ) : orders.map(order => (
          <div key={order.id} className="bg-white rounded-xl p-4 shadow border">
            <div className="flex justify-between items-start">
              <div>
                <h2 className="font-semibold">Pedido #{order.id}</h2>
                <p className="text-sm text-gray-500">Cliente: {clients[order.clientId] || 'N/A'}</p>
                <p className="text-sm text-gray-500">Vendedor: {reps[order.userId] || 'N/A'}</p>
                <p className="text-sm text-gray-500">Itens: {order.items?.length || 0}</p>
              </div>
              <div className="text-right">
                <div className="font-bold text-blue-600">R$ {order.totalPrice ? parseFloat(order.totalPrice).toFixed(2) : '0.00'}</div>
                <div className={`mt-2 inline-block px-2 py-1 text-xs font-semibold rounded-full ${
                  order.status === 'Entregue' ? 'bg-green-100 text-green-800' :
                  order.status === 'Em aberto' ? 'bg-yellow-100 text-yellow-800' : 'bg-gray-100 text-gray-800'
                }`}>
                  {order.status || 'N/A'}
                </div>
              </div>
            </div>

            <div className="flex gap-2 mt-4">
              <button onClick={() => openModal('delete', order.id)} className="text-red-500 text-sm">Excluir</button>
              {order.status === 'Em aberto' && <button onClick={() => openModal('finalize', order.id)} className="text-green-600 text-sm">Finalizar</button>}
              {order.status === 'Entregue' && <button onClick={() => handleGeneratePdf(order)} className="bg-gray-200 text-sm px-3 py-1 rounded">Gerar PDF</button>}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default OrdersPage;
