const generatePdfContent = (doc) => {
  // Logo (se tiver a imagem base64 ou public path)
  // doc.addImage(logoBase64, "PNG", 10, 8, 30, 15);

  // Cabeçalho empresa
  doc.setFontSize(12);
  doc.setFont("helvetica", "bold");
  doc.text("Blumenau Ind. e Com. Ltda", 200, 15, { align: "right" });
  doc.setFontSize(9);
  doc.setFont("helvetica", "normal");
  doc.text("CNPJ: 00.000.000/0001-00", 200, 22, { align: "right" });
  doc.text("Telefone: (47) 99999-9999", 200, 28, { align: "right" });

  // Título
  doc.setFontSize(14);
  doc.setFont("helvetica", "bold");
  doc.text("Pedido de Venda", 105, 40, { align: "center" });

  // Informações do Pedido
  doc.setFontSize(10);
  doc.setFont("helvetica", "normal");
  let startY = 55;
  doc.text(`Pedido #: ${orderToPdf.id}`, 10, startY);
  doc.text(`Cliente: ${clients[orderToPdf.clientId] || "N/A"}`, 10, startY + 7);
  doc.text(`Vendedor: ${reps[orderToPdf.userId] || "N/A"}`, 10, startY + 14);
  doc.text(
    `Data de Finalização: ${
      orderToPdf.finishedAt
        ? new Date(orderToPdf.finishedAt).toLocaleDateString("pt-BR")
        : "N/A"
    }`,
    10,
    startY + 21
  );
  doc.text(`Descrição: ${orderToPdf.description || "N/A"}`, 10, startY + 28);

  // Tabela de itens
  doc.autoTable({
    startY: startY + 40,
    head: [["Produto", "Marca", "Qtd.", "Preço Unitário", "Total"]],
    body: orderToPdf.items.map((item) => [
      item.productName,
      item.brand,
      item.quantity,
      `R$ ${parseFloat(item.price || 0).toFixed(2)}`,
      `R$ ${(parseFloat(item.price || 0) * item.quantity).toFixed(2)}`,
    ]),
    theme: "grid",
    headStyles: { fillColor: [200, 200, 200], textColor: [0, 0, 0], halign: "center" },
    bodyStyles: { fontSize: 9 },
    alternateRowStyles: { fillColor: [245, 245, 245] },
    columnStyles: {
      2: { halign: "right" },
      3: { halign: "right" },
      4: { halign: "right" },
    },
  });

  // Totais
  const finalY = doc.lastAutoTable.finalY + 10;
  const discountAmount =
    parseFloat(orderToPdf.totalPrice || 0) *
      (parseFloat(orderToPdf.discount || 0) / 100) || 0;

  doc.setFont("helvetica", "normal");
  doc.setFontSize(10);
  doc.text(`Desconto: R$ ${discountAmount.toFixed(2)} (${orderToPdf.discount || 0}%)`, 150, finalY);
  doc.setFont("helvetica", "bold");
  doc.text(
    `Valor Total: R$ ${parseFloat(orderToPdf.totalPrice || 0).toFixed(2)}`,
    150,
    finalY + 7
  );

  // Rodapé
  const footerData = companyInfo[selectedPdfBrand] || companyInfo.default;
  const footerY = doc.internal.pageSize.height - 10;
  doc.setFontSize(8);
  doc.setTextColor(150);
  doc.text(footerData.text, 105, footerY, { align: "center" });

  doc.save(`pedido_${orderToPdf.id}_${selectedPdfBrand}.pdf`);
  setShowPdfModal(false);
  setOrderToPdf(null);
};
